using UnityEngine;
using System.Collections;

public class ParticleResizeContainer
{
	public ParticleSystem[] particles;
	public float[] startSize;
	public float[] startSpeed;
	public int particleCount;

	public GameObject gameObject;

	public ParticleResizeContainer(GameObject go)
	{
		gameObject = go;

		particles = go.GetComponentsInChildren<ParticleSystem>();

		if(particles != null)
		{
			particleCount = particles.Length;

			startSize = new float[particleCount];
			startSpeed = new float[particleCount];

			for(int i = 0; i < particleCount ; ++i)
			{
				startSize[i] = particles[i].startSize;
				startSpeed[i] = particles[i].startSpeed;
			}
		}
	}


	public void destroy()
	{
		particles = null;
		startSize = null;
		startSpeed = null;
		GameObject.DestroyImmediate( gameObject, true);
		gameObject = null;
	}


	public void resize(float scale = 1.0f)
	{
//		_v = tf.localScale;
//		_v.x = scale;
//		_v.y = scale;
//		_v.z = scale;
//		tf.localScale = _v;
		
		for(int i = 0; i < particleCount; ++i)
		{
			particles[i].startSize = startSize[i] * scale;
			particles[i].startSpeed = startSpeed[i] * scale;
		}

		for(int i = 0; i < particleCount; ++i)
		{
			particles[i].Play(false);
		}
	}

}

